function related_results_labels_thumbs(e){for(var t=0;t<e.feed.entry.length;t++){var l=e.feed.entry[t];if(relatedTitles[relatedTitlesNum]=l.title.$t,relatedAuthors[relatedTitlesNum]=l.author[0].name.$t,relatedTime[relatedTitlesNum]=new Date(l.published.$t).getTime(),relatedcmt[relatedTitlesNum]=l.link[1].title,l.media$thumbnail){var r=l.media$thumbnail.url;r=r.replace("/s72-c","/w600-h400-c"),thumburl[relatedTitlesNum]=r}else thumburl[relatedTitlesNum]="http://2.bp.blogspot.com/-ex3V86fj4dQ/UrCQQa4cLsI/AAAAAAAAFdA/j2FCTmGOrog/w600-h400-c/no-thumbnail.png";for(var a=0;a<l.link.length;a++)"alternate"==l.link[a].rel&&(relatedUrls[relatedTitlesNum]=l.link[a].href,relatedTitlesNum++)}}function removeRelatedDuplicates_thumbs(){for(var e=new Array(0),t=new Array(0),l=new Array(0),r=new Array(0),a=new Array(0),s=new Array(0),d=0;d<relatedUrls.length;d++)contains_thumbs(e,relatedUrls[d])||(e.length+=1,e[e.length-1]=relatedUrls[d],t.length+=1,l.length+=1,r.length+=1,a.length+=1,s.length+=1,t[t.length-1]=relatedTitles[d],l[l.length-1]=thumburl[d],r[r.length-1]=relatedAuthors[d],a[a.length-1]=relatedTime[d],s[s.length-1]=relatedcmt[d]);relatedTitles=t,relatedUrls=e,thumburl=l,relatedAuthors=r,relatedTime=a,relatedcmt=s}function contains_thumbs(e,t){for(var l=0;l<e.length;l++)if(e[l]==t)return!0;return!1}function printRelatedLabels_thumbs(){for(t=0;t<relatedUrls.length;t++)relatedUrls[t]!=currentposturl&&relatedTitles[t]||(relatedUrls.splice(t,1),relatedTitles.splice(t,1),thumburl.splice(t,1),relatedAuthors.splice(t,1),relatedTime.splice(t,1),relatedcmt.splice(t,1),t--);for(var e=Math.floor((relatedTitles.length-1)*Math.random()),t=0;t<relatedTitles.length&&t<20&&t<maxresults;)document.write('<div class="col-md-6"><div class="related-item"><div class="entry-media"><div class="tw-thumbnail"><img src="'+thumburl[e]+'" alt="3"><div class="image-overlay tw-middle"><div class="image-overlay-inner"><a href="'+relatedUrls[e]+'" title="'+relatedTitles[e]+'" class="overlay-icon"></a></div></div></div></div><h2 class="entry-title"><a href="'+relatedUrls[e]+'">'+relatedTitles[e]+'</a></h2><div class="tw-meta"><span class="entry-author">by&nbsp;<a href="'+relatedUrls[e]+'" title="Posts by '+relatedAuthors[e]+'" rel="author">'+relatedAuthors[e]+'</a></span><span class="entry-date"><a href="'+relatedUrls[e]+'">'+timeSince(relatedTime[e])+' ago</a></span><span class="comment-count"><a href="'+relatedUrls[e]+'#comments" title="'+relatedcmt[e]+'">'+relatedcmt[e]+"</a></span></div></div></div>"),e<relatedTitles.length-1?e++:e=0,t++;relatedUrls.splice(0,relatedUrls.length),thumburl.splice(0,thumburl.length),relatedTitles.splice(0,relatedTitles.length),relatedAuthors.splice(0,relatedAuthors.length),relatedTime.splice(0,relatedTime.length),relatedcmt.splice(0,relatedcmt.length)}var relatedTitles=new Array,relatedAuthors=new Array,relatedTime=new Array,relatedcmt=new Array,relatedTitlesNum=0,relatedUrls=new Array,thumburl=new Array;
